            thisDataPoint.y = myItem.count;
            newMsg[0].data[newMsg[0].series.indexOf(myItem._id.label)].push(thisDataPoint);
        }
        else {
            //date exists, sum counts
            newMsg[0].data[newMsg[0].series.indexOf(myItem._id.label)][xIndex].y += myItem.count;
        }

    };

