var $myZoneNearIndex = msg.payload.after.entered_zones.indexOf("zone_near");
var $myZoneFarIndex = msg.payload.after.entered_zones.indexOf("zone_far");

var $myDirection = ($myZoneNearIndex >= 0 && $myZoneNearIndex >= 0) ? ($myZoneFarIndex < $myZoneNearIndex ? "inbound" : "outbound") : undefined;

msg.payload.after.nr_direction = $myDirection;

msg.topic = "frigate_event";
return msg;

var query = "";
// query += "from(bucket: " + env.get("VAR_INFLUXDB_BUCKET") + ")"
query += "from(bucket: \"tm-05\")\n";
query += "    |> range(start: " + (Math.floor(msg.payload.after.start_time)-2) +", stop: " + (Math.ceil(msg.payload.after.end_time)+2) + ")\n";
query += "    |> filter(fn: (r) => r[\"_measurement\"] == \"DetectedObjectVelocity\")\n";
query += "    |> filter(fn: (r) => r[\"_field\"] == \"velocity\")\n";
query += "    |> filter(fn: (r) => r[\"location\"] == \"" + env.get("VAR_LOCATION") + "\")\n";
query += "    |> filter(fn: (r) => r[\"units\"] == \"mph\")\n";
query += "    |> median(column: \"_value\")\n"; 

msg.query = query;
return msg;

//if only 1 Radar.direction USE THAT
if (msg.payload.query_return_median.length == 1) {
    //  check if Event.nr_direction MATCHES Radar.direction THEN good
    if (msg.payload.frigate_event.after.nr_direction == msg.payload.query_return_median[0].direction) {
        msg.dir_speed_status = "direction_speed_match";
        msg.direction_match = msg.payload.frigate_event.after.nr_direction;
    }
    //  if Event.nr_direction == undefined THEN use 1 that came through radar 
    else if (msg.payload.frigate_event.after.nr_direction == undefined) {
        msg.dir_speed_status = "direction_speed_match";
        msg.direction_match = msg.payload.query_return_median[0].direction;
    }
}
//  multiple radar directions, but if Event.nr_direction MATCHES Radar.direction THEN good
else if ((msg.payload.query_return_median.length > 1)
    && (msg.payload.frigate_event.after.nr_direction != undefined)) {

    //find matching median value to frigate_event
    if (msg.payload.query_return_median.map((o) => o.direction).indexOf(msg.payload.frigate_event.after.nr_direction) >= 0) {
        msg.dir_speed_status = "direction_speed_match";
    }
    else {
        msg.dir_speed_status = "direction_speed_no_match";
        msg.direction_match = undefined;
    }
}
//      if == 0 Radar.direction DO NOT REPORT
else {
    msg.dir_speed_status = "direction_speed_no_match"; 
    msg.direction_match = undefined;
    }

return msg;

var newMsg = {};

newMsg.event_id = msg.payload.event_end_car_bike.after.id;
newMsg.frigate_ip = env.get("VAR_DEVICE_IP");

newMsg.payload = {
    "subLabel": String("speed_" + msg.payload.query_return[0].units + "_" + msg.payload.query_return[0].direction), 
    "subLabelScore": (Math.abs(msg.payload.query_return[0]._value)/1000)
    };

return newMsg;

const newMsg = {};
newMsg.payload = {}; // contain everything going into a DB record, single record
newMsg.payload.event = {}; // single collection of strings
newMsg.payload.radar = {}; // contain all desired radar readings

// Frigate Event info
newMsg.payload.event.id = msg.payload.frigate_event.after.id;
newMsg.payload.event.camera = msg.payload.frigate_event.after.camera;
newMsg.payload.event.frame_time = msg.payload.frigate_event.after.frame_time;
newMsg.payload.event.label = msg.payload.frigate_event.after.label;
newMsg.payload.event.sub_label = msg.payload.frigate_event.after.sub_label;
newMsg.payload.event.top_score = msg.payload.frigate_event.after.top_score;
newMsg.payload.event.start_time = msg.payload.frigate_event.after.start_time;
newMsg.payload.event.end_time = msg.payload.frigate_event.after.end_time;
newMsg.payload.event.entered_zones = msg.payload.frigate_event.after.entered_zones; //array
newMsg.payload.event.direction_calc = msg.direction_match;

// Attached Radar info

newMsg.payload.radar.query_return_median = msg.payload.query_return_median[0];

newMsg.payload.radar.query_return_nonagg = msg.payload.query_return_nonagg;

return newMsg;

